import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown, faCaretRight, faIndianRupeeSign } from '@fortawesome/free-solid-svg-icons';

function App() {
  const Menu = require('./data/menu.json');
  const [showMenu, setShowMenu] = React.useState(Object.fromEntries(Object.keys(Menu).map(category => [category, false])));
  console.log(showMenu);
  const toggleMenu = (category) => {
    setShowMenu({ ...showMenu, [category]: !showMenu[category] });
    console.log(showMenu);
  }

  return (
    <div className="menu-container">
      <h1 className='text-5xl menu-title'>New Rajshree Sweets Menu</h1>
      {Object.keys(Menu).map((category) => (
        <React.Fragment key={category}>
          <div className="category-container">
            <div className={`bg-rose-600 hover:bg-rose-700 text-white category-title-wrapper ${showMenu[category] ? 'active' : ''}`} onClick={toggleMenu}>
              <h1 className='text-2xl category-title'>{category}</h1>
              <FontAwesomeIcon icon={faCaretRight} className='caret-right' />
              <FontAwesomeIcon icon={faCaretDown} className='caret-down' />
            </div>
            <div className="items-container">
              {Menu[category].map((item, index) => (
                <div key={index} className={`item-wrapper ${index % 2 == 0 ? 'alternate' : ''}`} >
                  <span className='item-title'>{item.name}</span>
                  <span className='item-rate'><FontAwesomeIcon icon={faIndianRupeeSign} /> {item.rate}</span>
                </div>
              ))}
            </div>
          </div>
        </React.Fragment>
      ))
      }
    </div >
  );
}

export default App;
